import UIKit

protocol AccountPresentationLogic {
    func presentAccountDetails(response: UserAccountResponse)
    func presentStatements(response: StatementResponse)
    func presentError(error: Error)
}

final class AccountPresenter: AccountPresentationLogic {
  
    
    weak var viewController: AccountDisplayLogic?
    
    func presentAccountDetails(response: UserAccountResponse) {
        let viewModel = AccountViewModel(userAccount: response.userAccount)
        viewController?.displayAccountDetails(viewModel: viewModel)
    }
    
    func presentStatements(response: StatementResponse) {
        let list = response.statements.map({StatementViewModel.init(statement: $0)})
        viewController?.displayStatements(list: list)
    }
    
    func presentError(error: Error) {
        viewController?.presentError(error: error)
    }
}
