import UIKit

@objc protocol AccountRoutingLogic {
    func routeToLogin()
}

final class AccountRouter: NSObject, AccountRoutingLogic, AccountDataPassing {
    var dataStore: AccountDataStore?
    weak var viewController: AccountViewController?
    
    func routeToLogin() {
        guard let accountViewController = self.viewController else { return }
        accountViewController.dismiss(animated: true, completion: nil)
    }
}
