import UIKit
protocol LoginPresentationLogic {
    func presentError(error: Error)
    func presentUserAccount(response: UserAccountResponse)
    func presentPersistedCredentials(response: Login)
}

final class LoginPresenter: LoginPresentationLogic {
    weak var viewController: LoginDisplayLogic?

    func presentError(error: Error) {
        let viewModel = LoginViewModel(error: error)
        viewController?.displayError(viewModel: viewModel)
    }
    
    func presentUserAccount(response: UserAccountResponse) {
        viewController?.displayUserAccount()
    }
    
    func presentPersistedCredentials(response: Login) {
        let viewModel = LoginViewModel(user: response.user, password: response.password)
        viewController?.displayPersistedCredentials(viewModel: viewModel)
    }
    
    
}
